INSERT INTO tblEmailTemplate (\`TemplateID\`, \`CompanyID\`, \`TemplateName\`, \`Subject\`, \`TemplateBody\`, \`created_at\`, \`CreatedBy\`, \`updated_at\`, \`ModifiedBy\`, \`userID\`, \`Type\`, \`EmailFrom\`, \`StaticType\`, \`SystemType\`, \`Status\`, \`StatusDisabled\`, \`TicketTemplate\`) VALUES (52, 1, 'Agent - Note added to ticket', 'Note Added - [#{{TicketID}}] {{Subject}}', '<p>Hi ,&nbsp;<br><br>{{NoteUser}}&nbsp;added a note and wants you to have a look.</p><p><a href="{{TicketUrl}}" title="Link: {{TicketUrl}}">View</a></p>Subject:&nbsp;<br>{{Subject}}<br><br>Requester: {{RequesterName}}&nbsp;<br><br>Note Content:&nbsp;<br>{{Notebody}}<br><br><br>', '2017-01-25 14:33:46', 'System', '2017-03-22 17:47:10', 'System', NULL, 4, '${COMPANY_EMAIL}', 1, 'Noteaddedtoticket', 1, 0, 1);
INSERT INTO tblEmailTemplate (\`TemplateID\`, \`CompanyID\`, \`TemplateName\`, \`Subject\`, \`TemplateBody\`, \`created_at\`, \`CreatedBy\`, \`updated_at\`, \`ModifiedBy\`, \`userID\`, \`Type\`, \`EmailFrom\`, \`StaticType\`, \`SystemType\`, \`Status\`, \`StatusDisabled\`, \`TicketTemplate\`) VALUES (56, 1, 'Requester - Agent Resolved the Ticket', 'Ticket Resolved - [#{{TicketID}}] {{Subject}}', 'Dear {{RequesterName}},<br><br>Our Support Rep has indicated that your Ticket (#{{TicketID}}) has been Resolved.&nbsp;<br><br>If you believe that the ticket has not been resolved, please reply to this email to automatically reopen the ticket.<br>If there is no response from you, we will assume that the ticket has been resolved and the ticket will be automatically closed after 48 hours.<br><br>Sincerely,<br>{{helpdesk_name}}<br>Support Team<br>', '2017-01-25 14:36:11', 'System', '2017-02-22 13:06:20', 'System', NULL, 4, '${COMPANY_EMAIL}', 1, 'AgentSolvestheTicket', 1, 0, 1);
INSERT INTO tblEmailTemplate (\`TemplateID\`, \`CompanyID\`, \`TemplateName\`, \`Subject\`, \`TemplateBody\`, \`created_at\`, \`CreatedBy\`, \`updated_at\`, \`ModifiedBy\`, \`userID\`, \`Type\`, \`EmailFrom\`, \`StaticType\`, \`SystemType\`, \`Status\`, \`StatusDisabled\`, \`TicketTemplate\`) VALUES (57, 1, 'Requester - Agent Closes the Ticket', 'Ticket Closed - [#{{TicketID}}] {{Subject}}', 'Dear {{RequesterName}},<br><br>Your Ticket #{{TicketID}} - {{Subject}} -&nbsp; has been closed.<br><br>We hope that the ticket was resolved to your satisfaction. If you feel that the ticket should not be closed or if the ticket has not been resolved, please reply to this email.<br><br>Sincerely,<br>{{helpdesk_name}}<br>Support Team<br>', '2017-01-25 14:36:26', 'System', '2017-02-22 13:09:00', 'System', NULL, 4, '${COMPANY_EMAIL}', 1, 'AgentClosestheTicket', 1, 0, 1);
INSERT INTO tblEmailTemplate (\`TemplateID\`, \`CompanyID\`, \`TemplateName\`, \`Subject\`, \`TemplateBody\`, \`created_at\`, \`CreatedBy\`, \`updated_at\`, \`ModifiedBy\`, \`userID\`, \`Type\`, \`EmailFrom\`, \`StaticType\`, \`SystemType\`, \`Status\`, \`StatusDisabled\`, \`TicketTemplate\`) VALUES (61, 1, 'Requester - New Ticket Created', 'Ticket Received - [#{{TicketID}}] {{Subject}}', 'Dear {{RequesterName}},&nbsp;<br><br>We would like to acknowledge that we have received your request and a ticket has been created with Ticket ID - {{TicketID}}.<br>A support representative will be reviewing your request and will send you a personal response.(usually within 24 hours).<br><br><span>To view the status of the ticket or add comments, please visit&nbsp;</span><br>{{TicketCustomerUrl}}<br><br>Thank you for your patience.<br><br>Sincerely,<br><span>{{helpdesk_name}} Support Team</span><br>', '2017-01-25 15:08:14', 'System', '2017-02-22 12:43:57', 'System', NULL, 4, '${COMPANY_EMAIL}', 1, 'RequesterNewTicketCreated', 1, 0, 1);
